package baekjoon.BronzeSilver;
import java.util.*;

public class B2739 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		var sc = new Scanner(System.in);
		int n = sc.nextInt();
		for (int i = 1; i <= 9; i++) {
			System.out.println(n+" * "+i+" = "+(n*i));
		}
	}

}
